import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import "../styles/cart.scss";

const Cart = (props) => {
  const cartItems = props.productItems;
  const [totalPrice, setTotalPrice] = useState(0);

  //TODO: Change variable price to something else... works for now but it looks awful.

  const getTotalPrice = () => {
    cartItems.map((item) => {
      setTotalPrice(totalPrice + item.price);
    });

    return totalPrice;
  };

  return (
    <div className="cart__container">
      <div className="container__content">
        <h1>My order</h1>
        {cartItems.length === 0 && (
          <div className="content__empty">
            <h2>Your cart is empty</h2>
          </div>
        )}
        {cartItems.map((item) => {
          return (
            <div key={item.id} className="content__orders">
              <div className="orders__item">
                <p>{item.name}</p>
                <p>{item.price}zł</p>
              </div>
            </div>
          );
        })}
        <div className="content__total">
          <h2>{setTotalPrice}zł</h2>
          <Link to="/checkout" className="btn__checkout">
            Checkout
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Cart;
