import Header from "./components/Header";
import Footer from "./components/Footer";
import SiteRoutes from "./Routes/SiteRoutes";
import { BrowserRouter } from "react-router-dom";
import { useState, useEffect } from "react";

function App() {
  let orders = [
    {
      id: 1,
      name: "iKebap Pro Max Super",
      price: 22,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 2,
      name: "iKebap Pro Max",
      price: 21,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 3,
      name: "iKebap Classic",
      price: 15,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 4,
      name: "iKebap California Reaper",
      price: 18,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 5,
      name: "iKebap Overpriced",
      price: 37,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 6,
      name: "iKebap Small",
      price: 10,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 7,
      name: "iKebap Wege",
      price: 17,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 8,
      name: "iKebap American",
      price: 16,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
    {
      id: 9,
      name: "iKebap Góralski",
      price: 17,
      desc: "Lorem Ipsum dolor sit amet, consectetur adipsicing elit",
    },
  ];

  const [totalPrice, setTotalPrice] = useState(0);
  const [username, setUsername] = useState();
  const [orderItems, setOrderItems] = useState([]);
  const [isUserLoggedIn, setIsUserLoggedIn] = useState(false);
  const [menuItems, setMenuItems] = useState([]);

  useEffect(() => {
    fetch("/menu/items")
      .then((response) => response.json())
      .then((data) => setMenuItems(data));
    console.log(menuItems[8]);
  });

  const onAddOrder = (order) => {
    setOrderItems([...orderItems, order]);
  };

  const onPriceChange = (order) => {
    setTotalPrice(order);
  };

  const onUserIsLogged = (bool) => {
    setIsUserLoggedIn(bool);
  };

  const onUserLogIn = (user) => {
    setUsername(user);
  };

  const onUserLogOut = (bool) => {
    setIsUserLoggedIn(bool);
  };

  return (
    <div className="App">
      <BrowserRouter>
        <Header
          totalPrice={totalPrice}
          username={username}
          isUserLoggedIn={isUserLoggedIn}
          orderItems={orderItems.length}
          onUserIsLogged={onUserIsLogged}
          onUserLogOut={onUserLogOut}
        />
        <SiteRoutes
          orders={orders}
          onPriceChange={onPriceChange}
          orderItems={orderItems}
          totalPrice={totalPrice}
          onUserLogIn={onUserLogIn}
          onUserIsLogged={onUserIsLogged}
          onUserLogOut={onUserLogOut}
          onAddOrder={onAddOrder}
        />
        <Footer />
      </BrowserRouter>
    </div>
  );
}

export default App;
