import "../styles/card.scss";
import KebapImg from "../img/Kebab1.png";
import { useState } from "react";

//TODO: Create communication between CartComponent and Header to make an events;

const Card = (props) => {
  const [quantity, setQuantity] = useState(0);
  const [order, setOrder] = useState(props.order);

  const orderAndQuantityHandler = () => {
    setQuantity(quantity + 1);
    setOrder(props.order);
  };

  return (
    <div className="container__order">
      <div className="order__details">
        <img src={KebapImg} className="details__img" />
        <h3>{props.order.name}</h3>
        <p>{props.order.desc}</p>
        <div className="details__price-buy">
          <h1>{props.order.price}z≈Ç</h1>
          <input
            type="submit"
            value="Take it!"
            onClick={orderAndQuantityHandler}
            className="price-buy__btn"
          />
        </div>
      </div>
    </div>
  );
};

export default Card;
